const fetch = function(url, setting) { // fetchè¯·æ±‚çš„å°è£…
  let opts = { // è®¾ç½®å‚æ•°çš„åˆå§‹å€¼
    method: (setting.method || 'GET').toUpperCase(), // è¯·æ±‚æ–¹å¼
    headers: setting.headers || {}, // è¯·æ±‚å¤´è®¾ç½®
    credentials: setting.credentials || true, // è®¾ç½®cookieæ˜¯å¦ä¸€èµ·å‘é€
    body: setting.body || {},
    mode: setting.mode || 'no-cors', // å¯ä»¥è®¾ç½® cors, no-cors, same-origin
    redirect: setting.redirect || 'follow', // follow, error, manual
    cache: setting.cache || 'default' // è®¾ç½® cache æ¨¡å¼ (default, reload, no-cache)
  }
  let dataType = setting.dataType || 'json' // è§£ææ–¹å¼
  let data = setting.data || '' // å‚æ•°
  let paramsFormat = function(obj) { // å‚æ•°æ ¼å¼
    var str = ''
    for (var i in obj) {
      str += `${i}=${obj[i]}&`
    }
    return str.split('').slice(0, -1).join('')
  }

  if (opts.method === 'GET') {
    url = url + (data ? `?${paramsFormat(data)}` : '')
  } else {
    setting.body = data || {}
  }
  return new Promise((resolve, reject) => {
    fetch(url, opts).then(async res => {
      let data = dataType === 'text' ? await res.text() : dataType === 'blob' ? await res.blob() : await res.json()
      resolve(data)
    }).catch(e => {
      reject(e)
    })
  })
}

