//å»é™¤å­—ç¬¦ä¸²ç©ºæ ¼
const trim = function(str, type) { // å»é™¤ç©ºæ ¼ï¼Œ type:  1-æ‰€æœ‰ç©ºæ ¼  2-å‰åç©ºæ ¼  3-å‰ç©ºæ ¼ 4-åç©ºæ ¼
  type = type || 1
  switch (type) {
    case 1:
      return str.replace(/\s+/g, '')
    case 2:
      return str.replace(/(^\s*)|(\s*$)/g, '')
    case 3:
      return str.replace(/(^\s*)/g, '')
    case 4:
      return str.replace(/(\s*$)/g, '')
    default:
      return str
  }
