const store = { // æœ¬åœ°å­˜å‚¨
  set: function(name, value, day) { // è®¾ç½®
    let d = new Date()
    let time = 0
    day = (typeof(day) === 'undefined' || !day) ? 1 : day // æ—¶é—´,é»˜è®¤å­˜å‚¨1å¤©
    time = d.setHours(d.getHours() + (24 * day)) // æ¯«ç§’
    localStorage.setItem(name, JSON.stringify({
      data: value,
      time: time
    }))
  },
  get: function(name) { // è·å–
    let data = localStorage.getItem(name)
    if (!data) {
      return null
    }
    let obj = JSON.parse(data)
    if (new Date().getTime() > obj.time) { // è¿‡æœŸ
      localStorage.removeItem(name)
      return null
    } else {
      return obj.data
    }
  },
  clear: function(name) { // æ¸…ç©º
    if (name) { // åˆ é™¤é”®ä¸ºnameçš„ç¼“å­˜
      localStorage.removeItem(name)
    } else { // æ¸…ç©ºå…¨éƒ¨
      localStorage.clear()
    }
  }
}

const cookie = { // cookieæ“ä½œã€setï¼Œgetï¼Œdelã€‘
  set: function(name, value, day) {
    let oDate = new Date()
    oDate.setDate(oDate.getDate() + (day || 30))
    document.cookie = name + '=' + value + ';expires=' + oDate + "; path=/;"
  },
  get: function(name) {
    let str = document.cookie
    let arr = str.split('; ')
    for (let i = 0; i < arr.length; i++) {
      let newArr = arr[i].split('=')
      if (newArr[0] === name) {
        return newArr[1]
      }
    }
  },
  del: function(name) {
    this.set(name, '', -1)
  }
}

const getRealStyle = function(obj, styleName) { // Jsè·å–å…ƒç´ æ ·å¼ã€æ”¯æŒå†…è”ã€‘
  var realStyle = null
  if (obj.currentStyle) {
    realStyle = obj.currentStyle[styleName]
  } else if (window.getComputedStyle) {
    realStyle = window.getComputedStyle(obj, null)[styleName]
  }
  return realStyle
}

