const checkStr = function(str, type) { // å¸¸ç”¨æ­£åˆ™éªŒè¯ï¼Œæ³¨æ„typeå¤§å°å†™
  switch (type) {
    case 'phone': // æ‰‹æœºå·ç 
      return /^1[3|4|5|6|7|8|9][0-9]{9}$/.test(str)
    case 'tel': // åº§æœº
      return /^(0\d{2,3}-\d{7,8})(-\d{1,4})?$/.test(str)
    case 'card': // èº«ä»½è¯
      return /(^\d{15}$)|(^\d{18}$)|(^\d{17}(\d|X|x)$)/.test(str)
    case 'pwd': // å¯†ç ä»¥å­—æ¯å¼€å¤´ï¼Œé•¿åº¦åœ¨6~18ä¹‹é—´ï¼Œåªèƒ½åŒ…å«å­—æ¯ã€æ•°å­—å’Œä¸‹åˆ’çº¿
      return /^[a-zA-Z]\w{5,17}$/.test(str)
    case 'postal': // é‚®æ”¿ç¼–ç 
      return /[1-9]\d{5}(?!\d)/.test(str)
    case 'QQ': // QQå·
      return /^[1-9][0-9]{4,9}$/.test(str)
    case 'email': // é‚®ç®±
      return /^[\w-]+(.[\w-]+)*@[\w-]+(.[\w-]+)+$/.test(str)
    case 'money': // é‡‘é¢(å°æ•°ç‚¹2ä½)
      return /^\d*(?:.\d{0,2})?$/.test(str)
    case 'URL': // ç½‘å€
      return /(http|ftp|https)://[\w-_]+(.[\w-_]+)+([\w-.,@?^=%&:/~+#]*[\w-@?^=%&/~+#])?/.test(str)
    case 'IP': // IP
      return /((?:(?:25[0-5]|2[0-4]\d|[01]?\d?\d)\.){3}(?:25[0-5]|2[0-4]\d|[01]?\d?\d))/.test(str)
    case 'date': // æ—¥æœŸæ—¶é—´
      return /^(\d{4})-(\d{2})-(\d{2}) (\d{2})(?::\d{2}|:(\d{2}):(\d{2}))$/.test(str) ||
        /^(\d{4})-(\d{2})-(\d{2})$/.test(str)
    case 'number': // æ•°å­—
      return /^[0-9]$/.test(str)
    case 'english': // è‹±æ–‡
      return /^[a-zA-Z]+$/.test(str)
    case 'chinese': // ä¸­æ–‡
      return /^[\u4E00-\u9FA5]+$/.test(str)
    case 'lower': // å°å†™
      return /^[a-z]+$/.test(str)
    case 'upper': // å¤§å†™
      return /^[A-Z]+$/.test(str)
    case 'HTML': // HTMLæ ‡è®°
      return /<("[^"]*"|'[^']*'|[^'">])*>/.test(str)
    default:
      return true
  }
}

const dom = {
  $: function(selector) {
    let type = selector.substring(0, 1)
    if (type === '#') {
      if (document.querySelecotor) return document.querySelector(selector)
      return document.getElementById(selector.substring(1))
    } else if (type === '.') {
      if (document.querySelecotorAll) return document.querySelectorAll(selector)
      return document.getElementsByClassName(selector.substring(1))
    } else {
      return document['querySelectorAll' ? 'querySelectorAll' : 'getElementsByTagName'](selector)
    }
  },
  hasClass: function(ele, name) { /* æ£€æµ‹ç±»å */
    return ele.className.match(new RegExp('(\s|^)' + name + '(\s|$)'))
  },
  addClass: function(ele, name) { /* æ·»åŠ ç±»å */
    if (!this.hasClass(ele, name)) ele.className += ' ' + name
  },
  removeClass: function(ele, name) { /* åˆ é™¤ç±»å */
    if (this.hasClass(ele, name)) {
      let reg = new RegExp('(\s|^)' + name + '(\s|$)')
      ele.className = ele.className.replace(reg, '')
    }
  },
  replaceClass: function(ele, newName, oldName) { /* æ›¿æ¢ç±»å */
    this.removeClass(ele, oldName)
    this.addClass(ele, newName)
  },
  siblings: function(ele) { /* è·å–å…„å¼ŸèŠ‚ç‚¹ */
    console.log(ele.parentNode)
    let chid = ele.parentNode.children,
      eleMatch = []
    for (let i = 0, len = chid.length; i < len; i++) {
      if (chid[i] !== ele) {
        eleMatch.push(chid[i])
      }
    }
    return eleMatch
  },
  getByStyle: function(obj, name) { /* è·å–è¡Œé—´æ ·å¼å±æ€§ */
    if (obj.currentStyle) {
      return obj.currentStyle[name]
    } else {
      return getComputedStyle(obj, false)[name]
    }
  },
  domToStirng: function(htmlDOM) { /* DOMè½¬å­—ç¬¦ä¸² */
    var div = document.createElement('div')
    div.appendChild(htmlDOM)
    return div.innerHTML
  },
  stringToDom: function(htmlString) { /* å­—ç¬¦ä¸²è½¬DOM */
    var div = document.createElement('div')
    div.innerHTML = htmlString
    return div.children[0]
  }
}

