//count
result = people.reduce((acc, person) => acc + 1, 0);
console.log(result); // 3

//sum ages
result = people.reduce((acc, person) => acc + person.age, 0);
console.log(result); // 93

// array of names(map)
result = people.reduce((acc, person) => [...acc, person.name], []);
console.log(result); // ["Leigh", "Jenny", "Heather"]

//log
result = people.reduce((acc, person) => {
  //参数person.name是要迭代的元素，而...acc是要累加的数组。在这个表达式中，...acc表示将已经累加的数组展开，然后再添加上person.name作为新的元素，最终返回一个新的累加数组。
  const newAcc = [...acc, person.name]; // 更新累积值,acc 是一个累积的结果数组，初始值为一个空数组 [],每次迭代中，...acc 会展开之前累积的结果数组，然后将当前 person 对象的 name 属性插入到新数组中。因此，打印...acc 的值会是一个包含之前迭代过程中所有人名的数组。
  console.log(newAcc); // 输出每次迭代的结果 // ["Leigh"] // ["Leigh", "Jenny"] // ["Leigh", "Jenny", "Heather"]
  return newAcc; // 返回更新的累积值
}, []);

console.log(result); // 输出最终的 result // ["Leigh", "Jenny", "Heather"]

// convert to id => person lookup (dict) 以id为key，person为value,这样就可以通过id来查找person了,是更好的数据结构
result = people.reduce((acc, person) => {
  return { ...acc, [person.id]: person };
}, {});
console.log(result); // {1: {…}, 2: {…}, 3: {…}}
console.log(result["3"]); // {id: "3", name: "Heather", age: 28}

