}, null);
console.log(result); //28

// find by name
result = people.reduce((acc, person) => {
  //首先检查累加器是否已经被赋值，如果是则直接返回累加器的值，这样就可以避免在后续的迭代中再次查找到匹配的 person 对象。
  if (acc !== null) return acc;
  if (person.name === "Leigh") return person;
