}, null);
console.log(result); //{id: "1", name: "Leigh", age: 35}

//age all over 18 (every())
result = people.reduce((acc, person) => {
  //如果累加器acc的值为false（即之前已经有一个对象的年龄小于等于27岁），则直接返回false，不再继续遍历数组节省时间和资源
  if (!acc) return false;
