        { name: "小红", age: 23 },
        { name: "小青", age: 14 },
        { name: "小黄", age: 35 },
      ];
let persons = new Proxy(arr, {
        get(arr, prop) {
          return arr[prop];
        },
        set(arr, prop, value) { // set有三个参数
          arr[prop] = value;
