    listData.forEach(item => {
      const oItem = document.createElement('li');
      oItem.className = 'draggable-item';
      oItem.draggable = true;
      oItem.innerHTML = `<p>${ item }</p>`;
      oDraggableList.appendChild(oItem);
    });

    return oDraggableList;
  }

