//其中包含一个函数add和两个属性nextId和cache,核心理解：函数也是可以动态添加属性的
var store = {
//跟踪下一个要被赋值的Id
  nextId: 1,
//使用一个对象作为缓存,在其中存储函数
  cache: {},
  //仅当函数唯一时,将该函数加入缓存
  add: function (fn) {
    if (!fn.id) {
      //给函数动态添加属性
      fn.id = this.nextId++;
      this.cache[fn.id] = fn;
      return true;
    }
  },
};
