import {TIMEOUT_SEC} from './config.js'  //将配置的值用大写字母明示,方便阅读理解和配置

const timeout = function (s) {
  return new Promise(function (_, reject) {//某个位置的参数忽略但又要占位，就要这么写 '_',这里不需要resolved
     setTimeout(function () {
    	 reject(new Error(`Request took too long! Timeout after ${s} second`));
      }, s * 1000);
   });
};

export const getJSON = async function (url){
  try {
      //因为是promise对象所以必须await,用了race,这样实现了超时功能
      const res = await Promise.race([fetch(url), timeout(TIMEOUT_SEC)]); //TIMEOUT_SEC的值是10
      const data = await res.json(); //这)里会返回一个resovled 的值

      if (!res.ok) throw new Error(`${data.message} (${res.status})`);
      return data;//返回resovled 的值
   }catch (err){
      throw err;
  }
};

