// 发抖函数:在一定时间内，只执行最后一次操作，而忽略之前的操作,比如避免向后台频繁提交数据
export function debounce(fn, delay) {  
  let timerId;  

  return function(...args) {  
    clearTimeout(timerId); // 清除之前的等待时间  

    timerId = setTimeout(() => {  
      fn.apply(this, args);  
    }, delay);  
  };  
}
