/* 基于BASE64实现文件上传 */
(function () {
    let upload = document.querySelector('#upload2'),
        upload_inp = upload.querySelector('.upload_inp'),
        upload_button_select = upload.querySelector('.upload_button.select');

    // 验证是否处于可操作性状态
    const checkIsDisable = element => {
        let classList = element.classList;
        return classList.contains('disable') || classList.contains('loading');
    };

    // 把选择的文件读取成为BASE64,传一个file对象，转换为BASE64格式
    const changeBASE64 = file => {
        return new Promise(resolve => {
           //核心是这个 FileReader()是JS内置的方法,实现了BASE64
            let fileReader = new FileReader();
            fileReader.readAsDataURL(file);
            //fileReader需要异步获取所以用.onload
            fileReader.onload = ev => {
                resolve(ev.target.result);
            };
        });
    };

    upload_inp.addEventListener('change', async function () {
        let file = upload_inp.files[0],
            BASE64,
            data;
        if (!file) return;
        if (file.size > 2 * 1024 * 1024) {
            alert('上传的文件不能超过2MB~~');
            return;
        }
        upload_button_select.classList.add('loading');
        BASE64 = await changeBASE64(file);
        try {
            data = await instance.post('/upload_single_base64', { //instance是封装过的axios的实例
                file: encodeURIComponent(BASE64),//编码防止服务端乱码
                filename: file.name
            }, {
                headers: {
                     //BASE64需要这个文件头
                    'Content-Type': 'application/x-www-form-urlencoded'
                }
            });
            if (+data.code === 0) {
                alert(`恭喜您，文件上传成功，您可以基于 ${data.servicePath} 地址去访问~~`);
                return;
            }
            throw data.codeText; //这里抛出错误后，下面才可以catch到
        } catch (err) {
            alert('很遗憾，文件上传失败，请您稍后再试~~');
        } finally { //不论是成功还是失败都执行这里
            upload_button_select.classList.remove('loading');
        }
    });
    upload_button_select.addEventListener('click', function () {
        if (checkIsDisable(this)) return;
        upload_inp.click(); //upload_inp是页面里被隐藏的那个原生input按钮
    });
})();

