<div class="pagination">
  <span class="prev">上一页</span>
  <span class="page" data-target="content_1">1</span>
  <span class="page" data-target="content_2">2</span>
  <span class="next">下一页</span>
</div>

//------------------- JS
const pages = document.querySelectorAll(".page"); //这里是一个可以forEach的类数组
const contentContainers = document.querySelectorAll(".content");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");

let currentPageIndex = 0; //跟踪当前页面索引
// 初始化时隐藏prevBtn
prevBtn.style.display = "none";

pages.forEach((page, index) => {
    page.addEventListener("click", () => {
    //console.log("page= ",page, "index= ",index);
    // 点击页面时，根据索引切换到对应的内容容器
    showPage(index);
  });
});

prevBtn.addEventListener("click", () => {
  if (currentPageIndex > 0) {
    // 上一页按钮点击时，显示前一页
    showPage(currentPageIndex - 1);
  }
});

nextBtn.addEventListener("click", () => {
  if (currentPageIndex < pages.length - 1) {
    // 下一页按钮点击时，显示下一页
    showPage(currentPageIndex + 1);
  }
});

function showPage(index) {
  window.scrollTo({ top:670, behavior: "smooth" });//滚动触发内容显示
  // 隐藏当前页面
  contentContainers[currentPageIndex].classList.remove("active");
  //给分页的数字添加样式
  pages[currentPageIndex].classList.remove("active");


  // 显示新页面
  contentContainers[index].classList.add("active");
  pages[index].classList.add("active");
  currentPageIndex = index; // 更新当前页面索引

  // 根据当前页面索引显示/隐藏prevBtn和nextBtn
  if (currentPageIndex === 0) {
    prevBtn.style.display = "none";
  } else {
    prevBtn.style.display = "block";
  }

  if (currentPageIndex === pages.length - 1) {
    nextBtn.style.display = "none";
  } else {
    nextBtn.style.display = "block";
  }
}

捕获异常就是处理错误，当错误发生后，我们对错误进行相应的处理，让程序不至于终止
在绝大部分时候，我们都无须捕获异常，除非满足以下要求:
1.我们能够预知某段代码会出现异常
2.我们知道出现异常后要做什么
上面的条件任意一个不满足，都不应该处理异常永远不能为了不报错而捕获异常!
try{
   //代码1,错误也是对象，有的话会把错误对象传递给catch的err参数
   //有错误的话，不会继续执行try里下面的代码，会执行catch代码块
}
catch(err){
  // 代码2: 当代码1出现异常后，会执行这里的代码，异常对象会传递给err
  console.log(err.message)
finally{ //可省略
 // 代码3可省略。无论是否有异常，都会执行
}

