const fs = require('fs');
const superagent = require('superagent');

fs.readFile(`${__dirname}/dog.txt`, (err, data) => { //__dirname 表示绝对路径
  if (err) throw err;
  console.log(`Breed: ${data}`);

  //doing the HTTP request.
  superagent
    .get(`https://dog.ceo/api/breed/${data}/images/random`)
    .end((err, res) => {
      if (err) return console.log(err.message);
      console.log(`Random ${data} image: ${res.body.message}`);//data只是一个狗名称的d关键字

      fs.writeFile(`${__dirname}/dog-img.txt`, res.body.message, (err) => {
        if (err) return console.log(err.message);
        console.log('Random dog image saved to file!');
      });
    });
});

const fs = require('fs');
const superagent = require('superagent');

const readFilePromise = (filePath) => {
  return new Promise((resolve, reject) =>
    fs.readFile(filePath, (err, data) => (err ? reject(err) : resolve(data)))
  );
};

const writeFilePromise = (filePath, fileData) => {
  return new Promise((resolve, reject) =>
    fs.writeFile(filePath, fileData, (err) =>
      err ? reject(err) : resolve('success')
    )
  );
};

readFilePromise(`${__dirname}/dog.txt`)
  .then((data) => {
    console.log(`Breed: ${data}`);

    return superagent.get(`https://dog.ceo/api/breed/${data}/images/random`);
  })
  .then((res, data) => {
    // if (err) return console.log(err.message); 这个err没用因为then里面没有err,要放到catch里面
    console.log(res.body.message);

    return writeFilePromise(`dog-img.txt`, res.body.message);
  })
  .then(() => {
    console.log('Random dog image saved to file!');
  })
  .catch((err) => {  //这里能够捕获到整个链条上的任何错误
    console.log(err.message);
  });

