//创建一个新的 todo
app.post('/todos',(req,res)=>{
const {title,completed=false} = req.body; //解构
    if(!title){
      	return res.status(400).json({message:'Title is required'})
    }
    const newTodo = {
        id:nextId++,
        title,
        completed
    };
    todos.push(newTodo);
    res.status(201).json(newTodo);
})

