 		 this.$emit('switch-tab', AddBigService);// switch-tab是tab主页面的事件名称
  },
}

文件名为 crossTagMsg.js
创建两个函数，分别是发消息和监听消息，实现跨tab页通信并且实时更新第二个tab页
核心是利用localStorage或sessionStorage来作为共享存储空间。当一个标签页修改了存储中的某个键值对时，其他标签页可以通过监听storage事件来获取更新。注意是实时刷新的！
export function sendMsg(type, payload){
	localStorage.setItem(
  		'@@' + type, //是避免重复的意思
      JSON.stringify({
          //这里写成对象是为了加个属性包含唯一值
          payload,
          temp: Date.now(),
     })
   };
}
export function listenMsg(handler){
  const storageHandler = e =>{
     //这些很简单，看f12的数据结构即可！属性是e事件对象自带的
     const data = JSON.parse(e.newValue)
     //去除前面'@@'符号
     handler(e.key.substring(2),data.payload)
     windows.addEventListener('storage', storageHandler)
     //通用做法，这种监听函数都会返回一个新的函数，可以专门用来取消监听
     return ()=>{
       window.removeEventListener('storage',storageHandler)
     }
  }
}

//在列表页监听
