fileSizeFormatter(row) {
    const sizeUnit = ['', 'KB', 'M', 'G', 'T'];
    let val = row.fileSize;
    if (!val) return '0';
    const sizeType = val == 0 ? 0 : parseInt(Math.floor(Math.log(val) / Math.log(1024)).toString());
    const size = (val / Math.pow(1024, sizeType)).toFixed(2).replace('.00', '');
    return size + ' ' + sizeUnit[sizeType];
},
