const trim = function(str, type) { // å»é™¤ç©ºæ ¼ï¼Œ type:  1-æ‰€æœ‰ç©ºæ ¼  2-å‰åç©ºæ ¼  3-å‰ç©ºæ ¼ 4-åç©ºæ ¼
  type = type || 1
  switch (type) {
    case 1:
      return str.replace(/\s+/g, '')
    case 2:
      return str.replace(/(^\s*)|(\s*$)/g, '')
    case 3:
      return str.replace(/(^\s*)/g, '')
    case 4:
      return str.replace(/(\s*$)/g, '')
    default:
      return str
  }
}

//å­—ç¬¦ä¸²å¤§å°å†™è½¬æ¢

const changeCase = function(str, type) { // å­—ç¬¦ä¸²å¤§å°å†™è½¬æ¢ type:  1:é¦–å­—æ¯å¤§å†™  2ï¼šé¦–é¡µæ¯å°å†™  3ï¼šå¤§å°å†™è½¬æ¢  4ï¼šå…¨éƒ¨å¤§å†™  5ï¼šå…¨éƒ¨å°å†™
  type = type || 4
  switch (type) {
    case 1:
      return str.replace(/\b\w+\b/g, function(word) {
        return word.substring(0, 1).toUpperCase() + word.substring(1).toLowerCase()
      })
    case 2:
      return str.replace(/\b\w+\b/g, function(word) {
        return word.substring(0, 1).toLowerCase() + word.substring(1).toUpperCase()
      })
    case 3:
      return str.split('').map(function(word) {
        if (/[a-z]/.test(word)) {
          return word.toUpperCase()
        } else {
          return word.toLowerCase()
        }
      }).join('')
    case 4:
      return str.toUpperCase()
    case 5:
      return str.toLowerCase()
    default:
      return str
  }
}

