function countNodes(node) {
  let count = 1; // å½“å‰èŠ‚ç‚¹ç®—ä¸€ä¸ª
  if (node.children && node.children.length > 0) { // å¦‚æœæœ‰å­èŠ‚ç‚¹
    for (let i = 0; i < node.children.length; i++) { // éå†å­èŠ‚ç‚¹
      count += countNodes(node.children[i]); // é€’å½’è®¡ç®—å­èŠ‚ç‚¹æ•°é‡
    }
  }
  return count;
}

