import Router from 'vue-router'
import { constantRouterMap } from '@/config/router.config'

const originalPush = Router.prototype.push
Router.prototype.push = function push(location) {
  return originalPush.call(this, location).catch((err) => err)
}

Vue.use(Router)

const router = new Router({
  mode: 'history',
  base: process.env.BASE_URL,
  scrollBehavior: () => ({ y: 0 }),
  routes: constantRouterMap
})
//beforeEach() é’©å­å‡½æ•°ä¼šåœ¨æ¯æ¬¡è·¯ç”±è·³è½¬å‰è¢«è°ƒç”¨
router.beforeEach((to, from, next) => {
  let url = window.location.href
  let urls = url.split(':')
  //è¿™é‡Œçš„ urls[0] + ':' + urls[1] ä»£ç æ˜¯ä¸ºäº†è·å–å½“å‰è®¿é—®ç«™ç‚¹çš„åè®®å’Œä¸»æœºåœ°å€ï¼Œç»„æˆä¸€ä¸ªå­—ç¬¦ä¸²
  let realurl = urls[0] + ':' + urls[1]

  if (to.meta.title === 'æ¥å£ç®¡ç†') {
    let api_url = realurl + ':9230/account/login'
    window.open(api_url, '_blank')
    next(false)
