class PaginationView extends View {
  parentElement = document.querySelector('.pagination');

   //å‘å¸ƒè€…ä¸è®¢é˜…è€…æ¨¡å¼ï¼Œå‘å¸ƒè€…å°±æ˜¯ä¸ªå‡½æ•°ï¼Œå…¶å›è°ƒå‡½æ•°å°±æ˜¯è®¢é˜…è€…
   //è¿™é‡Œä½¿ç”¨äº†äº‹ä»¶ä»£ç†ï¼Œé€šè¿‡çˆ¶å…ƒç´ æ§åˆ¶å·¦å³å¯¼èˆªçš„ä¸¤ä¸ªæŒ‰é’®
  addHandlerClick(handler){
    this._parentElement.addEventListener('click', function(e){
      //closest()æ˜¯å¾ˆå…³é”®çš„ç”¨æ³•, å¯ä»¥ç”¨æ¥é€‰æ‹©çˆ¶å…ƒç´ 
    	const btn = e.target.closest('.btn--inline');
      if(!btn) return; //è¿™é‡Œæ˜¯ä¸ªå®ˆå«ï¼Œæ²¡æœ‰å€¼å°±è·³å‡ºï¼Œä»¥å…æ‰§è¡Œä¸‹é¢ä»£ç å‡ºç°æŠ¥é”™

      //+å·æ˜¯ä¸ºäº†è½¬æ¢ä¸ºæ•°å­—ç±»å‹,ä»æµè§ˆå™¨çš„consoleçš„è¿”å›å€¼é¢œè‰²å¯ä»¥åˆ¤æ–­å€¼çš„ç±»å‹æ˜¯å­—ç¬¦è¿˜æ˜¯æ•°å­—
      //datasetå¯ä»¥è·å–åœ¨htmlé‡Œè‡ªå®šä¹‰çš„dataå€¼
      const goToPage = +btn.dataset.goto 
      //goToPageæ˜¯é¡µç 
      handler(goToPage);
    });
  }

  _generateMarkup(){
     const curPage = this._data.page;
     const numPages = Math.ceil(this._data.results.length / this._data.resultsPerPage); 
     console.log(numPages);

   		// Page 1, and there are other pages
     if (curPage === 1 && numPages > 1 ){
      return `
        //è¿™é‡Œæ·»åŠ äº†è‡ªå®šä¹‰å±æ€§ data-goto
        <button data-goto="${curPage + 1}" class="btn--inline pagination__btn--next">
          <span>Page ${curPage + 1}</span>
          <svg class="search__icon">
            <use href="${icons}#icon-arrow-right"></use>
           </svg>
        </button>
      `;
     }
    // Last page
     if (curPage === numPages && numPages > 1 ){
      return `
       	<buttondata-goto="${curPage - 1}" data-goto="${curPage + 1}" class="btn--inline pagination__btn--prev">
        	<svg class="search__icon">
          		<use href="${icons}#icon-arrow-left"></use>
          </svg>
					<span>Page ${curPage - 1}</span>
        </button>
      `;
     }
    // Other page
      if (curPage < numPages){
       return `
       	<button class="btn--inline pagination__btn--prev">
        	<svg class="search__icon">
          		<use href="${icons}#icon-arrow-left"></use>
          </svg>
					<span>Page ${curPage - 1}</span>
        </button>
        <button class="btn--inline pagination__btn--next">
          <span>Page ${curPage + 1}</span>
          <svg class="search__icon">
            <use href="${icons}#icon-arrow-right"></use>
           </svg>
        </button>
      `;
     }
      // Page 1, and there are NO other pages,Only one page
      //åªæœ‰ä¸€é¡µå°±ä¸æ˜¾ç¤ºä»»ä½•å¯¼èˆªæŒ‰é’®
       return ''; 
    }
}

