class Queue
{
    constructor()
    {
        this.items = []
    }

    enqueue(value)
    {
        this.items.push(value) // add to the end,å¦‚æœæƒ³å°†æ•°æ®æ”¾åœ¨æ•°ç»„çš„å‰é¢ï¼Œå¯ä»¥ä½¿ç”¨unshift()æ–¹æ³•ã€‚
    }

    dequeue()
    {
        if(this.items.length > 0)
            return this.items.shift() // remove the first element
    }

    peek()
    {
        if(this.items.length > 0)
            return this.items[0]
    }

    print()
    {
        for(let i = 0; i < this.items.length; i++)
            console.log(this.items[i])
    }
}

