public class MoraGame {                                                                               

    // æµ‹è¯•                                                                                             
    public static void main(String[] args) {                                                          
        // åˆ›å»ºä¸€ä¸ªç©å®¶å¯¹è±¡                                                                                   
        Tom t = new Tom();                                                                            
        // ç”¨æ¥è®°å½•æœ€åè¾“èµ¢çš„æ¬¡æ•°                                                                                
        int isWinCount = 0;                                                                           

        // åˆ›å»ºä¸€ä¸ªäºŒç»´æ•°ç»„ï¼Œç”¨æ¥æ¥æ”¶å±€æ•°ï¼ŒTomå‡ºæ‹³æƒ…å†µä»¥åŠç”µè„‘å‡ºæ‹³æƒ…å†µ                                                            
        int[][] arr1 = new int[3][3];                                                                 
        int j = 0;                                                                                    

        // åˆ›å»ºä¸€ä¸ªä¸€ç»´æ•°ç»„ï¼Œç”¨æ¥æ¥æ”¶è¾“èµ¢æƒ…å†µ                                                                          
        String[] arr2 = new String[3];                                                                

        Scanner scanner = new Scanner(System.in);                                                     
        for (int i = 0; i < 3; i++) {   //æ¯”èµ›3æ¬¡                                                              
            // è·å–ç©å®¶å‡ºçš„æ‹³                                                                                
            System.out.println("è¯·è¾“å…¥ä½ è¦å‡ºçš„æ‹³ï¼ˆ0-æ‹³å¤´ï¼Œ1-å‰ªåˆ€ï¼Œ2-å¸ƒï¼‰ï¼š");                                           
            int num = scanner.nextInt();                                                              
            t.setTomGuessNum(num);                                                                    
            int tomGuess = t.getTomGuessNum();                                                        
            arr1[i][j + 1] = tomGuess;                                                                

            // è·å–ç”µè„‘å‡ºçš„æ‹³                                                                                
            int comGuess = t.computerNum();                                                           
            arr1[i][j + 2] = comGuess;                                                                

            // å°†ç©å®¶çŒœçš„æ‹³ä¸ç”µè„‘åšæ¯”è¾ƒ                                                                           
            String isWin = t.vsComputer();                                                            
            arr2[i] = isWin;                                                                          
            arr1[i][j] = t.count;                                                                     

            // å¯¹æ¯ä¸€å±€çš„æƒ…å†µè¿›è¡Œè¾“å‡º                                                                            
           System.out.println("=========================================");                           
            System.out.println("å±€æ•°\tç©å®¶çš„å‡ºæ‹³\tç”µè„‘çš„å‡ºæ‹³\tè¾“èµ¢æƒ…å†µ");                                             
            System.out.println(t.count + "\t" + tomGuess + "\t\t" + comGuess + "\t\t" + t.vsComputer());
            System.out.println("=========================================");                          
            System.out.println("\n\n");                                                               
            isWinCount = t.winCount(isWin);                                                           
        }    
        scanner.close();

        // å¯¹æ¸¸æˆçš„æœ€ç»ˆç»“æœè¿›è¡Œè¾“å‡º                                                                               
        System.out.println("å±€æ•°\tç©å®¶çš„å‡ºæ‹³\tç”µè„‘çš„å‡ºæ‹³\t\tè¾“èµ¢æƒ…å†µ");                                               
        for (int a = 0; a < arr1.length; a++) {                                                       
            for (int b = 0; b < arr1[a].length; b++) {                                                
                System.out.print(arr1[a][b] + "\t\t\t");                                              
            }                                                                                         

            System.out.print(arr2[a]);                                                                
            System.out.println();                                                                     
        }                                                                                             
        System.out.println("ä½ èµ¢äº†" + isWinCount + "æ¬¡");                                                 
    }                                                                                                 

}                                                                                                     

