    /**                                                                                               
     * æ¯”è¾ƒçŒœæ‹³çš„ç»“æœ                                                                                        
     * @return ç©å®¶èµ¢è¿”å›trueï¼Œå¦åˆ™è¿”å›false                                                                    
     */                                                                                               
    public String vsComputer() { 
		 //æ¯”è¾ƒå·§
        if (tomGuessNum == 0 && comGuessNum == 1) {                                                   
            return "ä½ èµ¢äº†";                                                                             
        } else if (tomGuessNum == 1 && comGuessNum == 2) {                                            
            return "ä½ èµ¢äº†";                                                                             
        } else if (tomGuessNum == 2 && comGuessNum == 0) {                                            
            return "ä½ èµ¢äº†";                                                                             
        } else if (tomGuessNum == comGuessNum){                                                       
            return "å¹³æ‰‹";                                                                              
        } else {                                                                                      
            return "ä½ è¾“äº†";                                                                             
        }                                                                                             
    }                                                                                                 

    /**                                                                                               
     * è®°å½•ç©å®¶èµ¢çš„æ¬¡æ•°                                                                                       
     * @return                                                                                        
     */                                                                                               
    public int winCount(String s) {                                                                   
        count++;    //æ§åˆ¶ç©çš„æ¬¡æ•°                                                                                   
        if (s.equals("ä½ èµ¢äº†")) {     //ç»Ÿè®¡èµ¢çš„æ¬¡æ•°                                                                   
            winCountNum++;                                                                            
        }                                                                                             
        return winCountNum;                                                                           
    }                                                                                                 

}

