const pages = document.querySelectorAll(".page"); //è¿™é‡Œæ˜¯ä¸€ä¸ªå¯ä»¥forEachçš„ç±»æ•°ç»„
const contentContainers = document.querySelectorAll(".content");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");

let currentPageIndex = 0; //è·Ÿè¸ªå½“å‰é¡µé¢ç´¢å¼•
// åˆå§‹åŒ–æ—¶éšè—prevBtn
prevBtn.style.display = "none";

pages.forEach((page, index) => {
    page.addEventListener("click", () => {
    //console.log("page= ",page, "index= ",index);
    // ç‚¹å‡»é¡µé¢æ—¶ï¼Œæ ¹æ®ç´¢å¼•åˆ‡æ¢åˆ°å¯¹åº”çš„å†…å®¹å®¹å™¨
    showPage(index);
  });
});

prevBtn.addEventListener("click", () => {
  if (currentPageIndex > 0) {
    // ä¸Šä¸€é¡µæŒ‰é’®ç‚¹å‡»æ—¶ï¼Œæ˜¾ç¤ºå‰ä¸€é¡µ
    showPage(currentPageIndex - 1);
  }
});

nextBtn.addEventListener("click", () => {
  if (currentPageIndex < pages.length - 1) {
    // ä¸‹ä¸€é¡µæŒ‰é’®ç‚¹å‡»æ—¶ï¼Œæ˜¾ç¤ºä¸‹ä¸€é¡µ
    showPage(currentPageIndex + 1);
  }
});

function showPage(index) {
  window.scrollTo({ top:670, behavior: "smooth" });//æ»šåŠ¨è§¦å‘å†…å®¹æ˜¾ç¤º
  // éšè—å½“å‰é¡µé¢
  contentContainers[currentPageIndex].classList.remove("active");
  //ç»™åˆ†é¡µçš„æ•°å­—æ·»åŠ æ ·å¼
  pages[currentPageIndex].classList.remove("active");


  // æ˜¾ç¤ºæ–°é¡µé¢
  contentContainers[index].classList.add("active");
  pages[index].classList.add("active");
  currentPageIndex = index; // æ›´æ–°å½“å‰é¡µé¢ç´¢å¼•

  // æ ¹æ®å½“å‰é¡µé¢ç´¢å¼•æ˜¾ç¤º/éšè—prevBtnå’ŒnextBtn
  if (currentPageIndex === 0) {
    prevBtn.style.display = "none";
  } else {
    prevBtn.style.display = "block";
  }

  if (currentPageIndex === pages.length - 1) {
    nextBtn.style.display = "none";
  } else {
    nextBtn.style.display = "block";
  }
}

